import React, { useState } from 'react';
import api, { axiosSanctum } from '../../services/api';

const SignIn = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async () => {
        // Gửi yêu cầu GET để lấy CSRF cookie
        await axios.get('http://127.0.0.1:8000/api/sanctum/csrf-cookie', {
            withCredentials: true,  // Đảm bảo gửi cookie trong các yêu cầu tiếp theo
        });

        // Sau khi nhận CSRF cookie, gửi yêu cầu đăng nhập
        axios.post('http://127.0.0.1:8000/api/login', {
            ten_dang_nhap: 'admin',
            mat_khau: '123456',
        }, {
            withCredentials: true,  // Đảm bảo gửi cookie CSRF với yêu cầu đăng nhập
        })
            .then(response => {
                console.log('Login successful:', response);
            })
            .catch(error => {
                console.error('Login failed:', error);
            });
    };

    return (
        <div>
            <h2>Đăng nhập</h2>
            <input
                type="text"
                placeholder="Tên đăng nhập"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <input
                type="password"
                placeholder="Mật khẩu"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Đăng nhập</button>
        </div>
    );
};

export default SignIn;
