import React from 'react';

const DashBoardLists = () => {
    // Dữ liệu mẫu cho 3 danh sách
    const devicesExpiring = [
        { id: 1, name: 'Laptop Dell', expirationDate: '2025-04-25' },
        { id: 2, name: 'Máy chiếu Epson', expirationDate: '2025-04-30' },
        { id: 3, name: 'Smartphone iPhone', expirationDate: '2025-04-28' },
    ];

    const devicesAwaitingApproval = [
        { id: 1, name: 'Máy in Canon', requestDate: '2025-04-10' },
        { id: 2, name: 'Bàn phím Logitech', requestDate: '2025-04-12' },
    ];

    const devicesAwaitingReturn = [
        { id: 1, name: 'Tai nghe Bose', requestDate: '2025-04-05' },
        { id: 2, name: 'Máy tính xách tay HP', requestDate: '2025-04-08' },
    ];

    return (
        <div className="w-full px-6 py-6 mx-auto">
            {/* Thiết bị sắp hết hạn trong tháng */}
            <div className="mb-8">
                <h3 className="text-xl font-semibold mb-4">Danh sách thiết bị sắp hết hạn trong tháng</h3>
                <div className="bg-white shadow-lg rounded-lg p-4">
                    <ul>
                        {devicesExpiring.map(device => (
                            <li key={device.id} className="flex justify-between items-center border-b py-2">
                                <span>{device.name}</span>
                                <span className="text-gray-500">{device.expirationDate}</span>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>

            {/* Thiết bị đang chờ duyệt cấp */}
            <div className="mb-8">
                <h3 className="text-xl font-semibold mb-4">Danh sách thiết bị chờ duyệt cấp</h3>
                <div className="bg-white shadow-lg rounded-lg p-4">
                    <ul>
                        {devicesAwaitingApproval.map(device => (
                            <li key={device.id} className="flex justify-between items-center border-b py-2">
                                <span>{device.name}</span>
                                <span className="text-gray-500">{device.requestDate}</span>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>

            {/* Thiết bị đang chờ duyệt trả */}
            <div className="mb-8">
                <h3 className="text-xl font-semibold mb-4">Danh sách thiết bị chờ duyệt trả</h3>
                <div className="bg-white shadow-lg rounded-lg p-4">
                    <ul>
                        {devicesAwaitingReturn.map(device => (
                            <li key={device.id} className="flex justify-between items-center border-b py-2">
                                <span>{device.name}</span>
                                <span className="text-gray-500">{device.requestDate}</span>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default DashBoardLists;
