import React, { useState } from 'react';
import api from "../../services/api"; // Import axios instance

function SignIn() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);

    const handleLogin = async () => {
        try {
            // Lấy CSRF token trước khi gửi yêu cầu đăng nhập
            await api.get('/sanctum/csrf-cookie', { baseURL: 'http://127.0.0.1:8000' }); // KHÔNG dùng /api


            // Gửi yêu cầu POST đến backend để đăng nhập
            const response = await api.post('/login', {
                ten_dang_nhap: username,
                mat_khau: password
            });

            // Lưu token vào localStorage
            localStorage.setItem('token', response.data.token);  // Lưu token vào localStorage

            alert('Đăng nhập thành công!');
            // Có thể redirect tới trang khác sau khi đăng nhập thành công
        } catch (error) {
            // Xử lý lỗi và hiển thị thông báo lỗi
            setError(error.response?.data?.message || 'Đăng nhập thất bại');
        }
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Tên đăng nhập"
                value={username}
                onChange={e => setUsername(e.target.value)}
            />
            <input
                type="password"
                placeholder="Mật khẩu"
                value={password}
                onChange={e => setPassword(e.target.value)}
            />
            {error && <div>{error}</div>}  {/* Hiển thị lỗi nếu có */}
            <button onClick={handleLogin}>Đăng nhập</button>
        </div>
    );
}

export default SignIn;
