import axios from 'axios';

// Cấu hình Axios
const api = axios.create({
    baseURL: 'http://127.0.0.1:8000/api', // URL của Laravel API của bạn
    headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
    },
    withCredentials: true, // Cho phép gửi cookie (cần thiết cho CSRF và Sanctum)
});

// Gửi yêu cầu CSRF Cookie (được gọi trước khi gửi bất kỳ yêu cầu đăng nhập nào)
export const getCSRFToken = async () => {
    await api.get('/sanctum/csrf-cookie');
};

// Thêm interceptor để xử lý token xác thực
api.interceptors.request.use(
    config => {
        const token = localStorage.getItem('token');
        if (token) {
            config.headers.Authorization = `Bearer ${token}`; // Thêm Bearer Token vào header
        }
        return config;
    },
    error => Promise.reject(error)
);

// Interceptor xử lý response
api.interceptors.response.use(
    response => response.data, // Trả về chỉ data của response
    error => {
        console.error('API Error:', error);
        return Promise.reject(error);
    }
);

export default api;
