import React from 'react';

const DashBoardLists = () => {
    const devicesExpiring = [
        { id: 1, name: 'Laptop Dell', expirationDate: '2025-04-25' },
        { id: 2, name: 'Máy chiếu Epson', expirationDate: '2025-04-30' },
        { id: 3, name: 'Smartphone iPhone', expirationDate: '2025-04-28' },
    ];

    const devicesAwaitingApproval = [
        { id: 1, name: 'Máy in Canon', requestDate: '2025-04-10' },
        { id: 2, name: 'Bàn phím Logitech', requestDate: '2025-04-12' },
    ];

    const devicesAwaitingReturn = [
        { id: 1, name: 'Tai nghe Bose', requestDate: '2025-04-05' },
        { id: 2, name: 'Máy tính xách tay HP', requestDate: '2025-04-08' },
    ];

    return (
        <div className="w-full px-6 py-6 mx-auto">
            {/* Cards row */}
            <div className="flex justify-between space-x-6 mb-8">
                {/* Card 1 */}
                <div className="flex flex-col bg-white shadow-lg rounded-lg p-4 text-center w-1/3">
                    <h3 className="text-lg font-semibold text-gray-800">Thiết bị chưa sử dụng</h3>
                    <p className="text-2xl font-bold text-blue-500">120</p>
                    <div className="mt-4 bg-gradient-to-tl from-blue-500 to-violet-500 p-3 rounded-full text-white">
                        <i className="ni ni-box-2 text-xl"></i>
                    </div>
                </div>

                {/* Card 2 */}
                <div className="flex flex-col bg-white shadow-lg rounded-lg p-4 text-center w-1/3">
                    <h3 className="text-lg font-semibold text-gray-800">Thiết bị đang sử dụng</h3>
                    <p className="text-2xl font-bold text-green-500">75</p>
                    <div className="mt-4 bg-gradient-to-tl from-green-500 to-teal-400 p-3 rounded-full text-white">
                        <i className="ni ni-mobile-button text-xl"></i>
                    </div>
                </div>

                {/* Card 3 */}
                <div className="flex flex-col bg-white shadow-lg rounded-lg p-4 text-center w-1/3">
                    <h3 className="text-lg font-semibold text-gray-800">Thiết bị hỏng</h3>
                    <p className="text-2xl font-bold text-red-500">10</p>
                    <div className="mt-4 bg-gradient-to-tl from-red-500 to-orange-400 p-3 rounded-full text-white">
                        <i className="ni ni-alert-circle-exc text-xl"></i>
                    </div>
                </div>
            </div>

            {/* List Section */}
            <div>
                <h2 className="text-xl font-semibold text-gray-800">Danh sách thiết bị sắp hết hạn</h2>
                <ul className="list-disc pl-5 mt-4">
                    {devicesExpiring.map(device => (
                        <li key={device.id}>{device.name} - {device.expirationDate}</li>
                    ))}
                </ul>

                <h2 className="text-xl font-semibold text-gray-800 mt-6">Danh sách thiết bị chờ duyệt cấp</h2>
                <ul className="list-disc pl-5 mt-4">
                    {devicesAwaitingApproval.map(device => (
                        <li key={device.id}>{device.name} - {device.requestDate}</li>
                    ))}
                </ul>

                <h2 className="text-xl font-semibold text-gray-800 mt-6">Danh sách thiết bị chờ duyệt trả</h2>
                <ul className="list-disc pl-5 mt-4">
                    {devicesAwaitingReturn.map(device => (
                        <li key={device.id}>{device.name} - {device.requestDate}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default DashBoardLists;
